<script lang=\"ts\">\n  let stats = {\n    totalTours: 27,\n    activeTours: 18,\n    totalUsers: 142,\n    activeBookings: 38\n  };\n</script>\n\n<div class=\"dashboard\">\n  <h1>Dashboard</h1>\n  \n  <div class=\"stats-grid\">\n    <div class=\"stat-card\">\n      <h3>Total Tours</h3>\n      <div class=\"stat-value\">{stats.totalTours}</div>\n    </div>\n    \n    <div class=\"stat-card\">\n      <h3>Active Tours</h3>\n      <div class=\"stat-value\">{stats.activeTours}</div>\n    </div>\n    \n    <div class=\"stat-card\">\n      <h3>Total Users</h3>\n      <div class=\"stat-value\">{stats.totalUsers}</div>\n    </div>\n    \n    <div class=\"stat-card\">\n      <h3>Active Bookings</h3>\n      <div class=\"stat-value\">{stats.activeBookings}</div>\n    </div>\n  </div>\n  \n  <div class=\"recent-activity\">\n    <h2>Recent Activity</h2>\n    <div class=\"activity-list\">\n      <div class=\"activity-item\">\n        <span class=\"activity-time\">2 hours ago</span>\n        <span class=\"activity-text\">New booking: Tour to Paris</span>\n      </div>\n      <div class=\"activity-item\">\n        <span class=\"activity-time\">3 hours ago</span>\n        <span class=\"activity-text\">New user registered: john.doe@example.com</span>\n      </div>\n      <div class=\"activity-item\">\n        <span class=\"activity-time\">5 hours ago</span>\n        <span class=\"activity-text\">Tour modified: Barcelona Weekend</span>\n      </div>\n      <div class=\"activity-item\">\n        <span class=\"activity-time\">1 day ago</span>\n        <span class=\"activity-text\">New tour added: Rome Adventure</span>\n      </div>\n    </div>\n  </div>\n</div>\n\n<style lang=\"scss\">\n  @import '@travel-booking/shared-lib/dist/styles/variables.scss';\n  @import '@travel-booking/shared-lib/dist/styles/mixins.scss';\n  \n  .dashboard {\n    h1 {\n      margin-bottom: $spacing-lg;\n      color: $text-dark;\n    }\n  }\n  \n  .stats-grid {\n    display: grid;\n    grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));\n    gap: $spacing-md;\n    margin-bottom: $spacing-xl;\n  }\n  \n  .stat-card {\n    @include card;\n    padding: $spacing-lg;\n    \n    h3 {\n      margin: 0 0 $spacing-md 0;\n      color: $text-muted;\n      font-size: $font-size-md;\n    }\n    \n    .stat-value {\n      font-size: $font-size-xxl;\n      font-weight: $font-weight-bold;\n      color: $primary-color;\n    }\n  }\n  \n  .recent-activity {\n    @include card;\n    \n    h2 {\n      margin-top: 0;\n      margin-bottom: $spacing-lg;\n      color: $text-dark;\n    }\n  }\n  \n  .activity-list {\n    display: flex;\n    flex-direction: column;\n    gap: $spacing-md;\n  }\n  \n  .activity-item {\n    display: flex;\n    gap: $spacing-md;\n    padding-bottom: $spacing-sm;\n    border-bottom: 1px solid rgba($text-muted, 0.2);\n    \n    .activity-time {\n      color: $text-muted;\n      min-width: 100px;\n    }\n    \n    .activity-text {\n      color: $text-dark;\n    }\n  }\n</style>